(()=>{"use strict";const e=document,r=(window,e.querySelector("#btn-search")),t=e.querySelector("#username");r.addEventListener("click",(()=>{let r=t.value.split(" ").join("");r.length&&setTimeout((()=>{fetch(`https://api.github.com/users/${r}`).then((e=>e.json())).then((r=>{(({avatar:r,name:t,twitter:n})=>{e.querySelector("#print-user").innerHTML=null==t||null==n?'\n            <div class="alert alert-dark" role="alert">\n                Usuario no encontrado\n            </div>\n            ':`\n            <img class="img-avatar" src=${r}/>\n            <h2 class="user-name">${t}</h2>\n            <h2 class="user-twitter">@${n}</h2>\n        `})({avatar:r.avatar_url,name:r.name,twitter:r.twitter_username}),fetch(r.repos_url).then((e=>e.json())).then((r=>r.map((r=>(({repoName:r,url:t})=>{e.querySelector("#print-user").innerHTML+=`\n        <div class="repo-container">\n            <h3 class="repo-name"><a href=${t}>${r}</a></h3>\n            <h3 class="repo-url"><a href=${t}>${t}</a></h3>\n        </div>\n    `})({repoName:r.name,url:r.html_url})))))}))}),2e3)}))})();